mapper.select:
select count(*) from book
where 1 = 1
and bookname = #{bookname}
and publisher = #{publisher}

select * from book
where 1 = 1
and bookname like #{bookname}
and publisher like #{publisher}

select * from book
where 1 = 1
and bookname = #{bookname}
and bookid = #{bookid}
and publisher = #{publisher}
and price = #{price}

mapper.update:
update book
set bookname = #{bookname}
, publisher = #{publisher}
, year = #{year}
, price = #{price}
, dtm = #{dtm}
, use_yn = #{use_yn}
, authid = #{authid}
where 1 = 1

and bookid = #{wherebook.bookid}
and bookname = #{wherebook.bookname}

mapper.delete:
delete from book
where 1 = 1
and bookid = #{bookid}
and bookname = #{bookname}
